import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
let AddProgramareComponent = class AddProgramareComponent {
    constructor(appointmentService, notifyService, userService, token) {
        this.appointmentService = appointmentService;
        this.notifyService = notifyService;
        this.userService = userService;
        this.token = token;
        this.submitted = false;
    }
    showToasterSuccess() {
        this.notifyService.showSuccess("Programare înregistrată cu succes !!");
    }
    ngOnInit() {
        this.currentUser = this.token.getUser();
        this.appointmentForm = new FormGroup({
            email: new FormControl('', Validators.required),
            data: new FormControl((new Date()).toISOString().substring(0, 10)),
            ora_incepere: new FormControl('', Validators.required),
            ora_finalizare: new FormControl('', Validators.required),
            descriere: new FormControl('', Validators.required),
            id_doctor: new FormControl('', Validators.required)
        });
        this.userService.findUser(this.currentUser.email).subscribe({
            next: (response) => {
                this.loggedInUser = response;
                console.log(this.loggedInUser);
            },
            error: (error) => {
                alert(error.message);
            }
        });
        this.userService.findUsersByEmail(this.loggedInUser.email).subscribe({
            next: (response) => {
                this.users = response;
                console.log(this.users);
            },
            error: (error) => {
                alert(error.message);
            }
        });
    }
    async onSubmit(appointmentForm) {
        this.submitted = true;
        if (appointmentForm.valid) {
            this.appointmentService.addAppointment(appointmentForm.value).subscribe({
                next: async (response) => {
                    console.log(response);
                    this.showToasterSuccess();
                    await new Promise(f => setTimeout(f, 2000));
                    window.location.reload();
                    this.submitted = false;
                },
                error: (error) => {
                    alert(error.message);
                }
            });
        }
    }
};
