import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
let AddTreatmentComponent = class AddTreatmentComponent {
    constructor(appointmentService, istoricService, notifyService, userService, treatmentService, diagnosticSerivce, token) {
        this.appointmentService = appointmentService;
        this.istoricService = istoricService;
        this.notifyService = notifyService;
        this.userService = userService;
        this.treatmentService = treatmentService;
        this.diagnosticSerivce = diagnosticSerivce;
        this.token = token;
        this.submitted = false;
    }
    showToasterSuccess() {
        this.notifyService.showSuccess("Date adaugate cu succes !!");
    }
    ngOnInit() {
        this.currentUser = this.token.getUser();
        this.treatmentForm = new FormGroup({
            email: new FormControl(''),
            treatment: new FormControl(''),
            diagnostic: new FormControl(''),
            pret: new FormControl('', [Validators.required, Validators.pattern('^[0-9-]+$')]),
            dinte: new FormControl('', Validators.required),
            id_doctor: new FormControl('')
        });
        this.userService.findUser(this.currentUser.email).subscribe({
            next: (response) => {
                this.loggedInUser = response;
                console.log(this.loggedInUser);
                this.userService.findUsersByEmail(this.loggedInUser.id).subscribe({
                    next: (response) => {
                        this.users = response;
                        console.log(this.users);
                    },
                    error: (error) => {
                        alert(error.message);
                    }
                });
            },
            error: (error) => {
                alert(error.message);
            }
        });
        this.getDiagnostics();
        this.getTreatments();
    }
    getDiagnostics() {
        this.diagnosticSerivce.getDiagnostics().subscribe({
            next: (response) => {
                this.diagnostics = response;
            },
            error: (error) => {
                alert(error.message);
            }
        });
    }
    getTreatments() {
        this.treatmentService.getTreatments().subscribe({
            next: (response) => {
                this.treatments = response;
            },
            error: (error) => {
                alert(error.message);
            }
        });
    }
    async onSubmit(treatmentForm) {
        this.submitted = true;
        if (treatmentForm.valid) {
            this.treatmentForm.value['id_doctor'] = this.loggedInUser.id;
            this.istoricService.addIstoric(treatmentForm.value).subscribe({
                next: async (response) => {
                    console.log(response);
                    this.showToasterSuccess();
                    await new Promise(f => setTimeout(f, 1000));
                    window.location.reload();
                    this.submitted = false;
                },
                error: (error) => {
                    alert(error.message);
                }
            });
        }
    }
};
