        this.notifyService = notifyService;
        this.token = token;
        this.userService = userService;
        this.formularService = formularService;
        this.submitted = false;
        this.afectiuniData = ['Infarct miocardic', 'Tensiune arteriala crescuta', 'Tulburari de ritm cardiac', 'Hepatita', 'Tuberculoza', 'Ulcer/gastrita', 'Boli renale', 'Diabet zaharat', 'Astm', 'HIV/SIDA/alte afectiuni care scad imunitatea',
